public class part2 {
    public double cgRatio(String dna){
        int totalCG = 0;
        for (int i = 0; i< dna.length(); i++){
            if(dna.toUpperCase().charAt(i) == 'C' || dna.toUpperCase().charAt(i) == 'G' ){
                totalCG +=1;
            }
        }
        return (double) totalCG / dna.length();
    }
    
    public void testCgRatio(){
        String dna =  "ATGCCATAG" ;
        System.out.println("Given dna is " + dna);
        System.out.println("The Cg ratio of given dna is " + cgRatio(dna));
    }
    
     public int countCTG(String dna) {
        int totalOccurences = 0;
        int start = 0;
        
        while(true) {
            int index = dna.toUpperCase().indexOf("CTG", start);
            
            if (index == -1) {
                break;
            }
            
            totalOccurences++;
            
            start = index + 3;
        }
        
        return totalOccurences;
    }
    
    public void testCountCTG() {
        String dna = "";
        System.out.println("CTG occurences on " + dna + " = " + countCTG(dna));
    }
}
